    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String username = txtUsername.getText();
    String password = new String(txtPassword.getPassword());

    // Placeholder logic for checking credentials
    if (username.isEmpty() || password.isEmpty()) {
        JOptionPane.showMessageDialog(this, "Please enter both username and password.");
        return;
    }

    // TODO: Check against the text file
    boolean userExists = false; // Placeholder. This will be replaced with actual file checking logic.

    if (!userExists) {
        int response = JOptionPane.showConfirmDialog(this, "User not found. Would you like to register?", "Register", JOptionPane.YES_NO_OPTION);
        if (response == JOptionPane.YES_OPTION) {
            String newUsername = JOptionPane.showInputDialog(this, "Enter new username:");
            if (newUsername != null && !newUsername.trim().isEmpty()) {
                String newPassword = JOptionPane.showInputDialog(this, "Enter new password:");
                if (newPassword != null && !newPassword.trim().isEmpty()) {
                    // TODO: Save newUsername and newPassword to the text file
                } else {
                    JOptionPane.showMessageDialog(this, "Registration failed. Please enter a valid password.");
                }
            } else {
                JOptionPane.showMessageDialog(this, "Registration failed. Please enter a valid username.");
            }
        }
    } else {
        // TODO: Handle successful login
    }
}                                        





